import { useState } from "react"

const Post=()=>{
    const [news,setNews]=useState({
        heading:"",
        author:"",
        desc:"",
        link:"",
        linkname:""
    })

    let x,val;


    const handleInputs=(e)=>{
        x=e.target.name;
        val=e.target.value;

        setNews({...news,[x]:val})
        console.log(x,"**",val);
    }



    return(
        <React.Fragment>
            <form method="POST">
            <input type="text" name="heading" placeholder="HEADING" value={news.heading} onChange={handleInputs} />
            <br/>
            <input type="text" name="email" placeholder="AUTHOR" value={news.author} onChange={handleInputs}/>
            <br/>
            <input type="text-area" name="description" placeholder="DESCRIPTION" value={news.desc} onChange={handleInputs}/>
            <br/>
            <input type="text" name="link" placeholder="LINK" value={news.link} onChange={handleInputs}/>
            <br/>
            <input type="text" name="linkname" placeholder="LINKTEXT" value={news.linkname} onChange={handleInputs}/>
            <br/>
            </form>
            <button type="submit" className="submit" value='POST' onClick={submitData}>POST NEWS</button>
        </React.Fragment>
    );
}


export default Page;
