/* eslint-disable jsx-a11y/anchor-is-valid */
import React from 'react';
//import $ from 'jquery';
class Sidebar extends React.Component{

    constructor(){
      super();
      this.state={

        show:true

    }

      // this.handleToggle=this.handleToggle.bind(this);
    }

    handleToggle=()=>{
      console.log('hello call');
      $(document).ready(function(){
        $('.sidebar-btn').click(function(){
          console.log('first call');
          $('.sidebar').toggleClass('active');
          $('.sidebar-btn').toggleClass('toggle');

        })
      });
    };
    render(){
    return (
      <div className="sidebar">
        <p>English <span>Hindi</span></p>
        <hr></hr>
        <h1>Contents</h1>
        <ul>
          <li><a className="menu-items" target="_blank" href="https://inshorts.com">All News</a></li>
          <li><a className="menu-items" href="#">Science</a></li>
          <li><a className="menu-items" href="#">Political</a></li>
          <li><a className="menu-items" href="#">Business</a></li>
          <li><a className="menu-items" href="#">Automobiles</a></li>
          <li><a className="menu-items" href="#">Automobiles</a></li>
          <li><a className="menu-items" href="#">Bollywood</a></li>
        </ul>
        <button onClick={this.handleToggle} className="sidebar-btn"><span></span></button>
        {/* <button onClick={()=>{this.setState({show:!this.state.show})}} className="sidebar-btn"><span></span>{ this.state.show? 'Hide' : 'Show'}</button> */}
      </div>
    );
  }
}



export default Sidebar;
